import{_ as l,c as i,o,ag as c}from"./chunks/framework.Dpoz9nJ7.js";const h=JSON.parse('{"title":"linux-04 Vim编辑器","description":"","frontmatter":{},"headers":[],"relativePath":"src/前端/Server/linux-04 vim编辑器.md","filePath":"src/前端/Server/linux-04 vim编辑器.md","lastUpdated":1710326451000}'),d={name:"src/前端/Server/linux-04 vim编辑器.md"};function a(t,e,r,n,p,s){return o(),i("div",null,[...e[0]||(e[0]=[c('<h1 id="linux-04-vim编辑器" tabindex="-1">linux-04 Vim编辑器 <a class="header-anchor" href="#linux-04-vim编辑器" aria-label="Permalink to &quot;linux-04 Vim编辑器&quot;">​</a></h1><h2 id="一、vim编辑器" tabindex="-1">一、vim编辑器 <a class="header-anchor" href="#一、vim编辑器" aria-label="Permalink to &quot;一、vim编辑器&quot;">​</a></h2><blockquote><p>vim与vi的区别，vi为黑色，vim为红色</p></blockquote><ol><li>三种状态：</li></ol><ul><li><p><code>Command</code>：任何输入都会作为编辑命令，而不会出现在屏幕上，任何输入都引起立即反映</p></li><li><p><code>Insert</code>：任何输入的数据都置于编辑寄存器，按ESC，可跳回command方式</p></li><li><p><code>Escape</code>：以&#39;:&#39;或者&#39;/&#39;为前导的指令，出现在屏幕的最下一行，任何输入都被当成特别的指令</p></li></ul><h3 id="离开命令" tabindex="-1">离开命令 <a class="header-anchor" href="#离开命令" aria-label="Permalink to &quot;离开命令&quot;">​</a></h3><ul><li><p><code>ZZ</code> 保存当前文件并退出Vi</p></li><li><p><code>:q!</code> 离开vi，并放弃刚在缓存区编辑的内容</p></li><li><p><code>:wq</code> 将缓冲区内的资料写入磁盘中，并离开vi</p></li><li><p><code>:x</code> 同:wq（:X是文件加密，需要区分一下）</p></li></ul><h3 id="进入输入模式" tabindex="-1">进入输入模式 <a class="header-anchor" href="#进入输入模式" aria-label="Permalink to &quot;进入输入模式&quot;">​</a></h3><ul><li><p><code>a</code> 光标之后输入</p></li><li><p><code>A</code> 行末输入</p></li><li><p><code>i</code> 光标之前输入</p></li><li><p><code>I</code> 行首输入</p></li><li><p><code>o</code> 向下新增一行</p></li><li><p><code>O</code> 向上新增一行</p></li></ul><h3 id="删除、修改、撤销" tabindex="-1">删除、修改、撤销 <a class="header-anchor" href="#删除、修改、撤销" aria-label="Permalink to &quot;删除、修改、撤销&quot;">​</a></h3><ul><li><p><code>x</code> 删除光标下的字</p></li><li><p><code>X</code> 删除光标之前的字</p></li><li><p><code>D</code> 删除当前光标至行尾的内容</p></li><li><p><code>dd</code> 删除整行</p></li><li><p><code>dgg</code> 删除当前光标至文件头的内容</p></li><li><p><code>:10,20d</code> 删除第10行至20行的资料</p></li><li><p><code>:10d</code> 删除第10行</p></li><li><p><code>:%d</code> 删除整个编辑缓冲区</p></li><li><p><code>cc</code> 修改整行的内容</p></li><li><p><code>r</code> 修改当前光标的文字</p></li><li><p><code>R</code> 进入修改模式直到ESC退出为止</p></li><li><p><code>u</code> 恢复最后一个指令之前的结果</p></li><li><p><code>U</code> 恢复光标该行的所有改变</p></li><li><p><code>ctrl+r</code> 恢复撤消前的内容</p></li></ul><h3 id="光标移动" tabindex="-1">光标移动 <a class="header-anchor" href="#光标移动" aria-label="Permalink to &quot;光标移动&quot;">​</a></h3><ul><li><code>m&lt;a-z&gt;</code> 设置书签<code>&lt;a-z&gt;</code></li><li><code>&lt;a-z&gt;</code> 移至书签<code>&lt;a-z&gt;</code>处</li><li><code>0</code> 移至行首</li><li><code>$</code> 移至行末</li><li><code>e</code> 移到下个字的最后一个字母</li><li><code>w</code> 移动到下个字的第一个字母</li><li><code>b</code> 移动到上个字的第一个字母</li><li><code>^</code> 移至该行的第一个字</li><li><code>H</code> 移至视窗的第一行</li><li><code>M</code> 移至视窗的中间那行</li><li><code>L</code> 移至视察的最后一行</li><li><code>G</code> 移至该文件的最后一行</li><li><code>+</code> 移至下一列的第一个字</li><li><code>-</code> 移至上一列的第一个字</li><li><code>:n</code> 移至该文件的第n列</li><li><code>n+</code> 移至光标所在位置之后的第n列</li><li><code>n-</code> 移至光标所在位置之前的第n列</li><li><code>&lt;ctrl&gt;&lt;g&gt;</code> 显示该行之行号、文件名称、文件中最末行之行号、光标所在行号占总行号之百分比</li></ul><h3 id="视窗的移动" tabindex="-1">视窗的移动 <a class="header-anchor" href="#视窗的移动" aria-label="Permalink to &quot;视窗的移动&quot;">​</a></h3><ul><li><code>ctrl+f</code> 视窗往下卷一页</li><li><code>ctrl+b</code> 视窗往上卷一页</li><li><code>ctrl+d</code> 视窗往下卷半页</li><li><code>ctrl+u</code> 视窗往上卷半页</li><li><code>ctrl+e</code> 视窗往下卷一行</li><li><code>ctrl+y</code> 视窗往上卷一行</li></ul><h3 id="选择、复制、剪切、删除" tabindex="-1">选择、复制、剪切、删除 <a class="header-anchor" href="#选择、复制、剪切、删除" aria-label="Permalink to &quot;选择、复制、剪切、删除&quot;">​</a></h3><ul><li><code>v</code> 从光标当前位置开始，经过的地方会被选中，再按v结束</li><li><code>V</code> 从光标当前行开始，光标经过的行都会被选中，再按一下V结束</li><li><code>ctrl+v</code> 从光标当前位置开始，选中光标起点和终点所构成的矩形区域，再按ctrl+v结束</li><li><code>ggVG</code> 选中全部的文本，其中gg为跳到行首，V选中整行，G末尾</li><li><code>d</code> 剪切</li><li><code>y</code> 复制</li><li><code>p</code> 粘贴</li><li><code>yy</code> 复制光标当前行</li></ul><h3 id="查找与替换" tabindex="-1">查找与替换 <a class="header-anchor" href="#查找与替换" aria-label="Permalink to &quot;查找与替换&quot;">​</a></h3><ul><li><code>/字串</code> 光标之后寻找</li><li><code>?字串</code> 光标之前寻找</li><li><code>n</code> 往下继续寻找下一个相同的字串</li><li><code>N</code> 往上继续寻找下一个相同的字串</li><li><code>%</code> 查找&#39;(&#39;,&#39;)&#39;,&#39;{&#39;,&#39;}&#39;的配对符</li><li><code>s</code> 搜寻某行列范围</li><li><code>g</code> 搜寻整个编辑缓冲区的资料</li><li><code>:1,$s/old/new/g</code> 将文件中所有old改成new</li><li><code>:10,20s/%^/ /</code> 将10行至20行资料的最前面插入5个空白</li><li><code>/字符串</code> 后边输入查询内容可保存至缓冲区，可用箭头上下进行以往内容选择</li><li>将光标移动在选定单词下方按*，则可以选中此单词作为查询字符</li></ul><h3 id="环境的设定" tabindex="-1">环境的设定 <a class="header-anchor" href="#环境的设定" aria-label="Permalink to &quot;环境的设定&quot;">​</a></h3><ul><li><code>:set all</code> 可设置的环境变量列表</li><li><code>:set</code> 环境变量的当前值</li><li><code>:set nu</code> 显示行号</li><li><code>:set nonu</code> 取消行号设定。</li><li><code>:set ai</code> 自动内缩。</li><li><code>:set noai</code> 取消自动内缩。</li></ul><p>(vim)</p><ul><li><code>:set ruler</code> 会在屏幕右下角显示当前光标所处位置，并随光移动而改变，占用屏幕空间较小，使用也比较方便，推荐使用。</li><li><code>:set hlsearch</code> 在使用查找功能时，会高亮显示所有匹配的内容。</li><li><code>:set nohlsearch</code> 关闭此功能。</li><li><code>:set incsearch</code> 使Vim在输入字符串的过程中，光标就可定位显示匹配点。</li><li><code>:set nowrapscan</code> 关闭查找自动回环功能，即查找到文件结尾处，结束查找；默认状态是自动回环</li></ul>',23)])])}const m=l(d,[["render",a]]);export{h as __pageData,m as default};
